const breakpoints = [0, 400, 1000]

const mq = breakpoints.map(
	bp => `@media (min-width: ${bp}px)`
)

mq.nul = mq[0]
mq.mob = mq[1]
mq.des = mq[2]


export default {

	colors:{
	   c_gray1: '#525564',
	   c_gray2: '#74828F',
	   c_gray3: '#BEB9B5',
	   c_blue: '#96C0CE',

	   c_red: '#C25B56',
	   c_ping: '#FEF6EB',
	},

	fontSizes: {
		small: '20px',
		nomral: '40px',
		big: '60px',
	},





	variants: {

		box: {
			borderWidth: '5px',
			borderStyle: 'solid',
			padding: '10px',
			margin: 'auto',
			'&:hover': {
				opacity: '0.5'
			},
			[mq[0]]: {
				width: '100%',
			},
			[mq[1]]: {
				width: '50%',

			},
		},




	},


	mq,
}
